            stroke-width="2"
            stroke="currentColor"
            class="sp-grid-line"
        ></line>

        <line
            class="sp-grid-line"
            x1="50"
            y1="50"
            [attr.x2]="posX"
            [attr.y2]="posY"
            stroke-width="2"
            stroke="currentColor"
        ></line>
        <circle
            class="sp-grid-handle"
            [attr.cx]="posX"
